var Util = {
    countElements: function (obj) {
        var _i = 0;
        for (var key in obj) {
            if (obj.hasOwnProperty(key))
                _i++;
        }
        return _i;
    },

    logEvent: function (message) {
        var message = message == undefined ? "" : Array.prototype.slice.call(arguments, 0).join(" ");
        var time = new Date(),
            lz = function (v) {
                if ((v + "").length == 1) {
                    return "0" + v;
                }
                return v;
            };
        message = lz(time.getHours()) + ":" + lz(time.getMinutes()) + ":" + lz(time.getSeconds()) + " " + lz(time.getDate()) + "." + lz(time.getMonth()) + "." + (time.getYear() + 1900) + " : " + message;
        console.log(message);
    }

};

module.exports = Util;